import React, { FC,useState, useEffect } from "react";
import { useDBload } from "../../utils/DBLoader/DBLoaderContext";
// import classNames from "classnames";

export interface ImTableProps {
    className?: string;
}
interface Iobject {
    [key: string]: any;
}
interface ImageItem {
    id: string;
    fileName: string; // 假设每行数据中有一个fileName字段
  }
const serverIp='192.168.6.213'
const serverPort = '4555'
const MTable: FC<ImTableProps> = (props) => {
    const { className } = props;
    const { result } = useDBload();
    console.log("result",result)
    const [imageURLs, setImageURLs] = useState<{ [key: string]: string }>({});
    const [hoveredImage, setHoveredImage] = useState<string | null>(null);

    useEffect(() => {
        if (result && result.length > 0) {
            const fetchImageURLs = async () => {
                const urls: { [key: string]: string } = {};
                for (const item of result as Iobject[]) {
                    const url = await fetch(`http://${serverIp}:${serverPort}/preview?folder=${item["编号"]}&fileName=thumb_${item["封面页"]}`, {
                        method: 'get',
                        headers: {
                            'Content-Type': 'application/json',
                        }
                    }).then(response => response.url);
                    urls[item["编号"]] = url;
                }
                setImageURLs(urls);
            };
            fetchImageURLs();
        }
    }, [result]);
    if(result===undefined || result.length===0) return null
    const handleMouseEnter = (url: string) => {
        setHoveredImage(url);
    };

    const handleMouseLeave = () => {
        setHoveredImage(null);
    };

    const headers = Object.keys(result[0])
    return (
        <div>
            <table>
                <thead>
                    <tr>
                        {headers.map((item, index) => (
                            <th key={index}>{item}</th>
                        ))}
                    </tr>
                </thead>
                
                {result?.map((item:Iobject, index) => (
                    <tr key={index}>
                        {headers.map((key, subIndex) => (
                            <td key={subIndex} style={key === "封面页" ?{ padding:0 }:{}}>{key === "封面页"
                                ? imageURLs[item["编号"]] ? <img src={imageURLs[item["编号"]]} alt="封面页" onMouseEnter={() => handleMouseEnter(imageURLs[item["编号"]])} onMouseLeave={handleMouseLeave}/> : 'Loading...'
                                : item[key]}</td>
                        ))}
                    </tr>
                ))}
            </table>
            {hoveredImage && (
                <div className="popup">
                    <img src={hoveredImage} alt="Large preview" />
                </div>
            )}
        </div>
        
    );
}

export default MTable;
