import React, { FC,useState, useEffect } from "react";
import { useDBload } from "../../utils/DBLoader/DBLoaderContext";
// import classNames from "classnames";

export interface ImTableProps {
    className?: string;
}
interface Iobject {
    [key: string]: any;
}
interface ImageItem {
    id: string;
    fileName: string; // 假设每行数据中有一个fileName字段
  }
const serverIp='192.168.6.213'
const serverPort = '4555'
const MTable: FC<ImTableProps> = (props) => {
    const { className } = props;
    const { result } = useDBload();
    console.log("result",result)
    if(result===undefined || result.length===0) return null
    const fetchImageURLs = async (item:Iobject) => {
        const url:string = await new Promise(async(resolve, reject) => {
            const response = await fetch(`http://${serverIp}:${serverPort}/preview?folder=${item["编号"]}&fileName=${item["封面页"]}`, {
                method: 'get',
                headers: {
                  'Content-Type': 'application/json',
                }
              });
              resolve(response.url);
        });
        if(url!==undefined && url!==null){
            
            console.log(url)
            return <img src={url}></img>
        }
        return "";   

    };

    const headers = Object.keys(result[0])
    return (
        <table>
            <thead>
                <tr>
                    {headers.map((item, index) => (
                        <th key={index}>{item}</th>
                    ))}
                </tr>
            </thead>
            
            {result?.map((item:Iobject, index) => (
                <tr key={index}>
                    {headers.map((key, subIndex) => (
                        <td key={subIndex}>{key==="封面页"?fetchImageURLs(item):item[key]}</td>
                    ))}
                </tr>
            ))}
        </table>
    );
}

export default MTable;
